function $id(e){return document.getElementById(e)}function output(e){var i=$id("messages");i.innerHTML=e+i.innerHTML}function parseFile(e){output("<p><strong>"+e.name+"</strong> ("+e.size+"bytes)</p>")}function fileSelectHandler(e){fileDragHover(e);var i=$id("messages");i.innerHTML="";for(var n=e.target.files||e.dataTransfer.files,t=0,r;r=n[t];t++)parseFile(r)}function init(){var e=$id("files");e.addEventListener("change",fileSelectHandler,!1)}window.File&&window.FileList&&window.FileReader&&init();